var connect = require('connect'),
    explore = require('./lib');

var port = 4444;

var opts = {
  hidden : false,
  icons  : true,
  view   : 'details',
  filter : function(file) { return file }
}

var root = '/Users/tomas';

var app = connect()
    .use(connect.logger({ format: 'dev' }))
    .use(explore(root, opts))
    .use(connect.static(root))

app.listen(port);
console.log('Listening on port ' + port);
